Insert into GAMETRACE.GAME (GAMEID,GAMENAME,GAMEPC,GAMERY,GAMEPSEC,GAMEPMIN,GAMEPHOUR,GAMEPDAY,GAMEPN,GIMG,GAMEDR,GAMEDESC,GAMEICON) values (1,'Warcraft III: Reign of Chaos',27,2002,55,49,12,7,11,'../../games/1.jpg','<game>
	<rule>
		<proc>war3.exe</proc>
		<file>war3.mpq</file>
