Insert into GAMETRACE.GAME (GAMEID,GAMENAME,GAMEPC,GAMERY,GAMEPSEC,GAMEPMIN,GAMEPHOUR,GAMEPDAY,GAMEPN,GIMG,GAMEDR,GAMEDESC,GAMEICON) values (326,'Star Wars: Galactic Battlegrounds',0,2001,0,0,0,0,0,'../../games/326.jpg','<game>
