Insert into GAMETRACE.GAME (GAMEID,GAMENAME,GAMEPC,GAMERY,GAMEPSEC,GAMEPMIN,GAMEPHOUR,GAMEPDAY,GAMEPN,GIMG,GAMEDR,GAMEDESC,GAMEICON) values (25,'Guild Wars',0,2005,0,0,0,0,0,'../../games/25.jpg','<game>
