Insert into GAMETRACE.GAME (GAMEID,GAMENAME,GAMEPC,GAMERY,GAMEPSEC,GAMEPMIN,GAMEPHOUR,GAMEPDAY,GAMEPN,GIMG,GAMEDR,GAMEDESC,GAMEICON) values (137,'Tom Clancy''s Splinter Cell: Double Agent',0,2006,0,0,0,0,0,'../../games/137.jpg','<game>
