Insert into GAMETRACE.GAME (GAMEID,GAMENAME,GAMEPC,GAMERY,GAMEPSEC,GAMEPMIN,GAMEPHOUR,GAMEPDAY,GAMEPN,GIMG,GAMEDR,GAMEDESC,GAMEICON) values (139,'Assassin''s Creed: Brotherhood',0,2010,0,0,0,0,0,'../../games/139.jpg','<game>
