Insert into GAMETRACE.GAME (GAMEID,GAMENAME,GAMEPC,GAMERY,GAMEPSEC,GAMEPMIN,GAMEPHOUR,GAMEPDAY,GAMEPN,GIMG,GAMEDR,GAMEDESC,GAMEICON) values (176,'The Elder Scrolls V: Skyrim',0,2011,0,0,0,0,0,'../../games/176.jpg','<game>
