Insert into GAMETRACE.GAME (GAMEID,GAMENAME,GAMEPC,GAMERY,GAMEPSEC,GAMEPMIN,GAMEPHOUR,GAMEPDAY,GAMEPN,GIMG,GAMEDR,GAMEDESC,GAMEICON) values (226,'Lara Croft and the Temple of Osiris',0,2014,0,0,0,0,0,'../../games/226.jpg','<game>
