Insert into GAMETRACE.GAME (GAMEID,GAMENAME,GAMEPC,GAMERY,GAMEPSEC,GAMEPMIN,GAMEPHOUR,GAMEPDAY,GAMEPN,GIMG,GAMEDR,GAMEDESC,GAMEICON) values (217,'Tomb Raider: The Angel of Darkness',0,2003,0,0,0,0,0,'../../games/217.jpg','<game>
