Insert into GAMETRACE.GAME (GAMEID,GAMENAME,GAMEPC,GAMERY,GAMEPSEC,GAMEPMIN,GAMEPHOUR,GAMEPDAY,GAMEPN,GIMG,GAMEDR,GAMEDESC,GAMEICON) values (288,'Kane ' || chr(38) || ' Lynch 2: Dog Days',0,2010,0,0,0,0,0,'../../games/288.jpg','<game>
