Insert into GAMETRACE.GAME (GAMEID,GAMENAME,GAMEPC,GAMERY,GAMEPSEC,GAMEPMIN,GAMEPHOUR,GAMEPDAY,GAMEPN,GIMG,GAMEDR,GAMEDESC,GAMEICON) values (437,'Warhammer 40,000: Dawn of War II',0,2009,0,0,0,0,0,'../../games/437.jpg','<game>
