Insert into GAMETRACE.GAME (GAMEID,GAMENAME,GAMEPC,GAMERY,GAMEPSEC,GAMEPMIN,GAMEPHOUR,GAMEPDAY,GAMEPN,GIMG,GAMEDR,GAMEDESC,GAMEICON) values (341,'Wolfenstein',0,2009,0,0,0,0,0,'../../games/341.jpg','<game>
