Insert into GAMETRACE.GAME (GAMEID,GAMENAME,GAMEPC,GAMERY,GAMEPSEC,GAMEPMIN,GAMEPHOUR,GAMEPDAY,GAMEPN,GIMG,GAMEDR,GAMEDESC,GAMEICON) values (445,'Grand Theft Auto: Episodes from Liberty City',0,2009,0,0,0,0,0,'../../games/445.jpg','<game>
