Insert into GAMETRACE.GAME (GAMEID,GAMENAME,GAMEPC,GAMERY,GAMEPSEC,GAMEPMIN,GAMEPHOUR,GAMEPDAY,GAMEPN,GIMG,GAMEDR,GAMEDESC,GAMEICON) values (560,'IL-2 Sturmovik: Cliffs of Dover',0,2011,0,0,0,0,0,'../../games/560.jpg','<game>
