Insert into GAMETRACE.GAME (GAMEID,GAMENAME,GAMEPC,GAMERY,GAMEPSEC,GAMEPMIN,GAMEPHOUR,GAMEPDAY,GAMEPN,GIMG,GAMEDR,GAMEDESC,GAMEICON) values (1357,'Blair Witch Volume 1: Rustin Parr',1,2000,29,0,0,0,1,'../../games/1357.jpg','<game>
