Insert into GAMETRACE.GAME (GAMEID,GAMENAME,GAMEPC,GAMERY,GAMEPSEC,GAMEPMIN,GAMEPHOUR,GAMEPDAY,GAMEPN,GIMG,GAMEDR,GAMEDESC,GAMEICON) values (545,'Supreme Commander: Forged Alliance',0,2007,0,0,0,0,0,'../../games/545.jpg','<game>
