Insert into GAMETRACE.GAME (GAMEID,GAMENAME,GAMEPC,GAMERY,GAMEPSEC,GAMEPMIN,GAMEPHOUR,GAMEPDAY,GAMEPN,GIMG,GAMEDR,GAMEDESC,GAMEICON) values (550,'AaaaaAAaaaAAAaaAAAAaAAAAA!!! נA Reckless Disregard for Gravity',0,2009,0,0,0,0,0,'../../games/550.jpg','<game>
