Insert into GAMETRACE.GAME (GAMEID,GAMENAME,GAMEPC,GAMERY,GAMEPSEC,GAMEPMIN,GAMEPHOUR,GAMEPDAY,GAMEPN,GIMG,GAMEDR,GAMEDESC,GAMEICON) values (816,'Tom Clancy''s The Division',0,2016,0,0,0,0,0,'../../games/816.jpg','<game>
