Insert into GAMETRACE.GAME (GAMEID,GAMENAME,GAMEPC,GAMERY,GAMEPSEC,GAMEPMIN,GAMEPHOUR,GAMEPDAY,GAMEPN,GIMG,GAMEDR,GAMEDESC,GAMEICON) values (761,'Ultima IV: Quest of the Avatar',1,1985,9,0,0,0,1,'../../games/761.jpg','<game>
	<rule>
		<proc>DOSBox.exe</proc>
