Insert into GAMETRACE.GAME (GAMEID,GAMENAME,GAMEPC,GAMERY,GAMEPSEC,GAMEPMIN,GAMEPHOUR,GAMEPDAY,GAMEPN,GIMG,GAMEDR,GAMEDESC,GAMEICON) values (767,'Ultima VII Part Two: Serpent Isle',1,1993,9,0,0,0,1,'../../games/767.jpg','<game>
