Insert into GAMETRACE.GAME (GAMEID,GAMENAME,GAMEPC,GAMERY,GAMEPSEC,GAMEPMIN,GAMEPHOUR,GAMEPDAY,GAMEPN,GIMG,GAMEDR,GAMEDESC,GAMEICON) values (979,'Warhammer 40,000: Dawn of War ֠Soulstorm',0,2008,0,0,0,0,0,'../../games/979.jpg','<game>
