Insert into GAMETRACE.GAME (GAMEID,GAMENAME,GAMEPC,GAMERY,GAMEPSEC,GAMEPMIN,GAMEPHOUR,GAMEPDAY,GAMEPN,GIMG,GAMEDR,GAMEDESC,GAMEICON) values (718,'Command ' || chr(38) || ' Conquer 4: Tiberian Twilight',0,2010,0,0,0,0,0,'../../games/718.jpg','<game>
