Insert into GAMETRACE.GAME (GAMEID,GAMENAME,GAMEPC,GAMERY,GAMEPSEC,GAMEPMIN,GAMEPHOUR,GAMEPDAY,GAMEPN,GIMG,GAMEDR,GAMEDESC,GAMEICON) values (721,'Command ' || chr(38) || ' Conquer: Yuri''s Revenge',0,2001,0,0,0,0,0,'../../games/721.jpg','<game>
