Insert into GAMETRACE.GAME (GAMEID,GAMENAME,GAMEPC,GAMERY,GAMEPSEC,GAMEPMIN,GAMEPHOUR,GAMEPDAY,GAMEPN,GIMG,GAMEDR,GAMEDESC,GAMEICON) values (1320,'Jagged Alliance 2: Wildfire',0,2004,0,0,0,0,0,'../../games/1320.jpg','<game>
