Insert into GAMETRACE.GAME (GAMEID,GAMENAME,GAMEPC,GAMERY,GAMEPSEC,GAMEPMIN,GAMEPHOUR,GAMEPDAY,GAMEPN,GIMG,GAMEDR,GAMEDESC,GAMEICON) values (442,'The Settlers: Rise of an Empire',0,2007,0,0,0,0,0,'../../games/442.jpg','<game>
	<rule>
		<proc>Settlers6.exe</proc>
