Insert into GAMETRACE.GAME (GAMEID,GAMENAME,GAMEPC,GAMERY,GAMEPSEC,GAMEPMIN,GAMEPHOUR,GAMEPDAY,GAMEPN,GIMG,GAMEDR,GAMEDESC,GAMEICON) values (800,'Escape Dead Island',0,2014,0,0,0,0,0,'../../games/800.jpg','<game>
