Insert into GAMETRACE.GAME (GAMEID,GAMENAME,GAMEPC,GAMERY,GAMEPSEC,GAMEPMIN,GAMEPHOUR,GAMEPDAY,GAMEPN,GIMG,GAMEDR,GAMEDESC,GAMEICON) values (1034,'Operation Flashpoint: Dragon Rising',0,2009,0,0,0,0,0,'../../games/1034.jpg','<game>
