Insert into GAMETRACE.GAME (GAMEID,GAMENAME,GAMEPC,GAMERY,GAMEPSEC,GAMEPMIN,GAMEPHOUR,GAMEPDAY,GAMEPN,GIMG,GAMEDR,GAMEDESC,GAMEICON) values (224,'Tomb Raider: Underworld',0,2008,0,0,0,0,0,'../../games/224.jpg','<game>
	<rule>
		<proc>tru.exe</proc>
